	(6228,978,'_content_0_list_items_0_image','field_5981df9934d9b'),
	(6229,978,'content_0_list_items_0_text','<p><strong>Belief in young people</strong></p><p>we recognize that young peoples concerns can only be well addressed by young people and hence our slogan Young People for Young People</p>'),
	(6230,978,'_content_0_list_items_0_text','field_5981dfa034d9c'),
