	(6235,978,'content_0_list_items_2_image',''),
	(6236,978,'_content_0_list_items_2_image','field_5981df9934d9b'),
	(6237,978,'content_0_list_items_2_text','<p><strong>Transparency and openness</strong></p><p>We believe in a culture of honesty and therefore employ an open door policy where all staff and volunteers can freely communicate with each other.</p>'),
